import {rootConstants} from '../_constants';

const initialState = {
  lessonData: {"id":[]},
  counter: 247,

  GrapIsLog: false,
  LessonIsLog: false,
  SchoolIsLog: false,

  graphLessonName: 'ss',
  graphLessonData: [],
  schoolName: 'MUIS',

}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { ...state, counter: 1111 };
    case rootConstants.ROOT_SUCCESS:
        return { ...state, counter: 111222};


    case rootConstants.MODAL_SCHOOL_TRUE:
        return { ...state, counter: 3333, SchoolIsLog: true, graphLessonName: action.data };
    case rootConstants.MODAL_SCHOOL_FALSE:
        return { ...state, counter: 3333, SchoolIsLog: false, graphLessonName: action.data };


    case rootConstants.MODAL_LESSON_TRUE:
        return { ...state, counter: 3333, LessonIsLog: true, graphLessonName: action.data };
    case rootConstants.MODAL_LESSON_FALSE:
        return { ...state, counter: 3333, LessonIsLog: false, graphLessonName: action.data };



    case rootConstants.MODAL_GRAP_TRUE:
        return { ...state, counter: 441111, GrapIsLog: true, graphLessonData: action.data };
    case rootConstants.MODAL_GRAP_FALSE:
        return { ...state, counter: 444222, GrapIsLog: false};


    case rootConstants.SCHOOL_NAME:
        return { ...state, counter: 5555111, schoolName: action.data};


    case rootConstants.LESSONS_DATA_PUSH:
        return { ...state, counter: 66661111, lessonData: initialState.lessonData["id"].push(action.data.id)};
    case rootConstants.LESSONS_DATA_POP:
        return { ...state, counter: 666622222, lessonData: initialState.lessonData["id"].splice(initialState.lessonData["id"].indexOf(action.data.id), 1 )};
    default:
      return state
  }
}
