import { createStore, applyMiddleware ,combineReducers} from 'redux';
import thunkMiddleware from 'redux-thunk';
import createSagaMiddleware from 'redux-saga';
import { createLogger } from 'redux-logger';
import lessonReducer from '../_reducers/lessonReducer';
import rootReducer from '../_reducers/rootReducer';
import {graphState, allLessonState, lessonChooseState, lessonPlanState } from './../_saga/sagas';


const loggerMiddleware = createLogger();
const sagaMiddleware = createSagaMiddleware()
const store = createStore(
        lessonReducer,
    applyMiddleware(
        thunkMiddleware,
        loggerMiddleware,
        sagaMiddleware
    )
);

sagaMiddleware.run(graphState)
sagaMiddleware.run(allLessonState)
sagaMiddleware.run(lessonChooseState)
sagaMiddleware.run(lessonPlanState)
export default store;
