export function graphDataFetch (lessonName) 
{
    return new Promise(async (resolve, reject) => 
    {
        try 
        {  
            fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
                },
                body: 	qs.stringify({'lessonname': lessonName})
            })
            .then((res) => res.json())
            .then((data) => {
                resolve(data)})
        } 
        catch(error) 
        {
            alert("4 doh laert")
            reject(error);
        }
    });
}

export function* graphData(lessonName) {
    try 
    {
        const lessonDatas = yield call(graphDataFetch, lessonName)
        yield put({type: rootConstants.MODAL_GRAP_TRUE ,data: lessonDatas})
    } 
    catch(error) 
    {
        alert("error")
    }
    finally 
    {
        if (yield cancelled()) 
        {
            alert("cancel modal data ")
        }
    }
}

export function* graphState() 
{
    while (true) 
    {
        const {data} = yield take(rootConstants.MODAL_GRAP_REQUEST)
        yield put({type: rootConstants.MODAL_GRAP_REQUEST ,data: data})
        const setData = yield fork(graphData, data)


