import useGrid from './useGrid'
import { GridDivProps, OneResolutionGrid } from './types'

const defaultProps: OneResolutionGrid = {
  marginTop: 0,
  marginRight: 'gutter/2',
  marginBottom: 'verticalGutter',
  marginLeft: 'gutter/2',
}

const forwardRef = React.forwardRef<HTMLDivElement, GridDivProps>(function Col(
  props,
  ref
) {
  const colProps =
    props.margin !== undefined
      ? props
      : {
          ...defaultProps,
          ...props,
        }
  const [shouldShow, gridProps] = useGrid(colProps)

  return shouldShow ? <div {...gridProps} ref={ref} /> : null
})

forwardRef.displayName = 'Col'

export default forwardRef
import * as React from 'react'
import { GymnastContextType } from '../types'
import Context from './context'
import defaults from '../defaults'

type ProviderProps = GymnastContextType & {
  children?: React.ReactNode
}

type ProviderState = GymnastContextType

class GymnastProvider extends React.Component<ProviderProps, ProviderState> {
  static getDerivedStateFromProps(props: ProviderProps, state: ProviderState) {
    const { children, ...restProps } = props

    return {
      ...state,
      ...restProps,
    }
  }

  state = defaults

  render() {
    const { state } = this
    const { children } = this.props

    return <Context.Provider value={state}>{children}</Context.Provider>
  }
}

export default (props: ProviderProps) => {
  const context = React.useContext(Context)

  return <GymnastProvider {...context} {...props} />
}
