import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
const whiteStyle = {
   background: 'white'
 };
 const redStyle = {
   background: 'blue'
 };
class dataPrints extends React.Component {
   constructor() {
      super();
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.state = {
         data: [],
         colors:"white",
         colors1:"red",
         hover: false
      }
   }
   hoverOff(id){
      this.props.lessonDataPush({id})
      this.setState({hover: true});

   }
   hoverOn(id){
      this.props.lessonDataPop({id})
      this.setState({hover: false});
   }




   render() {

      return (
         <div className={this.props.data.id + " " + "border border-dark rounded"} style={this.state.hover ? redStyle : whiteStyle} 
                onClick={() => this.state.hover ? this.hoverOn(this.props.data.id) : this.hoverOff(this.props.data.id)}>
            <a>
              {this.props.data.index}-
              {this.props.data.lesson_name}-
              ({this.props.data.kridit})-
              {this.props.data.season}
            </a>
         </div>
      );
   }
}



export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(dataPrints);
