import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {allActions} from '../../_actions'
import GridLayout from 'react-grid-layout';
import WidthProvider from '../_lessonPlan/WidthProvider'

const ResponsiveGridLayout = WidthProvider(GridLayout);

class OnePlanLessonModal extends React.Component {
    constructor () {
      super();
      this.state = {
        isState: false
      };

      this.handleClick = this.handleClick.bind(this);
    }


    handleClick(data, islog) {


      }

    render () {
      return (
        <div style={{background:"#e6e6e6"}} >

           <ReactModal 
             isOpen={this.props.planLessonDataModalIsLog}
             className=""
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.planLessonDataModalFalse()}
            >
            <ResponsiveGridLayout  cols={12} rowHeight={20} width={1300}>
              <div className="col-sm-12 font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded"  key="d" data-grid={{x: 0, y: 0, w: 12, h: 3, static: true}}><h1 class="text-primary" >Та сургуулиа сонгоно уу !!!</h1></div>
                {this.props.planLessonData.map((person, i) => 
                    <div className="row planBody shadow p-3 mb-5 bg-white rounded" 
                        key={person.id} 
                        data-grid={{x: 0, y: 1, w: 12, h: 2 }} >
                          <div className="col-sm-9">{person.lesson_name} - ({person.kridit})</div>
                          <div className="col-sm-3">
                          <button class="btn" onClick={() => this.props.planLessonDataPop(person)}><i class="fa fa-minus-circle"></i>remove</button>
                             <button class="btn" ><i class="fa fa-plus-circle"></i>bagsh nemeh</button>
                          </div>

                    </div>
                )}
            </ResponsiveGridLayout>








           </ReactModal>
        </div>
      );
    }
  }

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(OnePlanLessonModal);
