import React from 'react';
import './classCss.css';
import {  Link } from 'react-router-dom';
import {rootConstants} from '../_constants';
import { connect } from 'react-redux';
import  history  from '../_store/history';
import schoolData from '../_data/schoolData';
import PlanPrint from '../_components/_lessonPlan/planPrint';
import {allActions} from '../_actions'
import GridLayout from 'react-grid-layout';
import WidthProvider from '../_components/_lessonPlan/WidthProvider'

const ResponsiveGridLayout = WidthProvider(GridLayout);

const board = {
  columns: [
    {
      id: 1,
      title: 'Backlog',
      cards: [
        {
          id: 1,
          title: 'Add card',
          description: 'Add capability to add a card in a column'
        },
      ]
    },
    {
      id: 2,
      title: 'Doing',
      cards: [
        {
          id: 2,
          title: 'Drag-n-drop support',
          description: 'Move a card between the columns'
        },
      ]
    }
  ]
}
class classScreen extends React.Component {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
          isState: false,
          isActive: true,

        };
      }
      componentDidMount() {
        this.props.lessonPlanRequest("hihah");

      }


      handleClick(data, islog) {
        if(islog){
          this.props.modalSchoolFalse(data)
        }else{
          this.props.newSchoolName(data)
        }

      }

      render() {
        return (

          <div style={{background:"#e6e6e6"}} className="col-sm-12 layout">

            <ResponsiveGridLayout className="col-sm-12 layout" cols={12} rowHeight={20} width={1300}>
              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="d" data-grid={{x: 0, y: 0, w: 3, h: 1, static: true}}>1 Күрс намар</div>
                {this.props.lessonPlan[0].autumn1.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 0, y: 1, w: 3, h: 2 }} >{person.lesson_name} - ({person.kridit})</div>)}


              <div  className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="2v" data-grid={{x: 3, y: 0, w: 3, h: 1,static: true}}>1 Күрс хавар</div>
                {this.props.lessonPlan[1].spring1.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 3, y: 0, w: 3, h: 2}}>{person.lesson_name}</div>)}


              <div  className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="d22" data-grid={{x: 6, y: 0, w: 3, h: 1,static: true}}>2 Күрс намар</div>
                {this.props.lessonPlan[2].autumn2.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 6, y: 0, w: 3, h:2}}>{person.lesson_name}</div>)}


              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="dxc3" data-grid={{x: 9, y: 0, w: 3, h: 1,static: true}}>2 Күрс хавар</div>
                {this.props.lessonPlan[3].spring2.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 9, y: 0, w: 3, h: 2}}>{person.lesson_name} - ({person.kridit})</div>)}


              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="d3e" data-grid={{x: 0, y: 9, w: 3, h: 1,static: true}}>3 Күрс намар</div>
                {this.props.lessonPlan[4].autumn3.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 0, y: 9, w: 3, h: 1}}>{person.lesson_name} - ({person.kridit})</div>)}



              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="ds3" data-grid={{x: 3, y: 9, w: 3, h: 1,static: true}}>3 Күрс хавар</div>
                {this.props.lessonPlan[5].spring3.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 3, y: 9, w: 3, h: 1}}>{person.lesson_name} - ({person.kridit})</div>)}


              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="ddfd3" data-grid={{x: 6, y: 9, w: 3, h: 1,static: true}}>4 Күрс намар</div>
                {this.props.lessonPlan[6].autumn4.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 6, y: 9, w: 3, h: 1}}>{person.lesson_name} - ({person.kridit})</div>)}

              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded" key="dss3" data-grid={{x: 9, y: 9, w: 3, h: 1,static: true}}>4 Күрс хавар</div>
                {this.props.lessonPlan[7].spring4.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 9, y: 9, w: 3, h: 1}}>{person.lesson_name} - ({person.kridit})</div>)}
            </ResponsiveGridLayout>
          </div>





        )

    }
}


export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(classScreen);
