import React from 'react';
import CanvasJSReact from '../canvas.js/canvasjs.react';
import qs from 'qs';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions';

import { connect } from 'react-redux';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class amountChart extends React.Component {
	constructor() {
		super();
		this.toggleDataSeries = this.toggleDataSeries.bind(this);
	}


	toggleDataSeries(e){
		if (typeof(e.dataSeries.visible) === "undefined" || e.dataSeries.visible) {
			e.dataSeries.visible = false;
		}
		else{
			e.dataSeries.visible = true;
		}
		this.chart.render();
	}

	render() {
		const autumn = this.props.graphLessonData.datedata[2].autumn;
		const spring = this.props.graphLessonData.datedata[1].spring;
		const dates = this.props.graphLessonData.datedata[3].date;
		const lessonName = this.props.graphLessonData.datedata[0].lessonName;
		const options = {
			theme: "light2",
			animationEnabled: true,
			exportEnabled: true,

			title:{
				text: lessonName
			},
			subtitles: [{
				text: ""
			}],
			axisX: {
                title: "Graph",
			},
			axisY: {
				title: dates +  " намар",
				titleFontColor: "#6D78AD",
				lineColor: "#6D78AD",
				labelFontColor: "#6D78AD",
				tickColor: "#6D78AD",
				includeZero: false
			},
			axisY2: {
				title:   dates + " хавар",
				titleFontColor: "#51CDA0",
				lineColor: "#51CDA0",
				labelFontColor: "#51CDA0",
				tickColor: "#51CDA0",
				includeZero: false
			},
			toolTip: {
				shared: true
			},
			legend: {
				cursor: "pointer",
				itemclick: this.toggleDataSeries
			},
			data: [{
				type: "spline",
				name: "Намар",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "#,##0 ",
				dataPoints: autumn
			},
			{
				type: "spline",
				name: "Хавар",
				axisYType: "secondary",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "",
				dataPoints: spring
			}]
		}


		return (
		<div>
			<CanvasJSChart options = {options} onRef={ref => this.chart = ref}/>
		</div>
		);
	}

}

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(amountChart);
