import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
const whiteStyle = {
   background: 'white'
 };
 const redStyle = {
   background: 'blue'
 };
class dataPrints extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.modalState = this.modalState.bind(this);
      this.rootStatecolor = this.rootStatecolor.bind(this);
      this.state = {
         data: [],
         colors:"white",
         colors1:"red",
         hover: false
      }
   }
   handleClick(person) {
      alert("sdfsd")
   }
   hoverOn(data){
      this.props.lessonDataAll({data})
      this.setState((prevState) => ({
         hover: !prevState.hover
       }));

   }
   hoverOff(){
      this.setState({ colors: "white" });
   }

   modalState(data){
      this.props.chartState(data);
   }

   rootStatecolor(data){
      document.getElementsByClassName(data)[0].style.background = 'black';
      document.getElementsByClassName("105")[0].style.background = 'black';
      document.getElementsByClassName("107")[0].style.background = 'black';
      document.getElementsByClassName("109")[0].style.background = 'black';
   }


   render() {

      return (
         <table style={this.state.hover ? redStyle : whiteStyle} className={this.props.data.id}
                onClick={() => this.hoverOn(this.props.data.id)}>
            <tr style={this.state.hover ? redStyle : whiteStyle} >
                  <td>{this.props.data.index}-</td>
                  <td>{this.props.data.lesson_name}-</td>
                  <td>({this.props.data.kridit})-</td>
                  <td>{this.props.data.season}</td>
                  <td>{JSON.stringify(this.props.lessonData)}</td>
            </tr>
         </table>
      );
   }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter,
      islog: state.islog,
      Reqislog: state.Reqislog,
      graphLessonName: state.graphLessonName,
      graphLessonData: state.graphLessonData,
      schoolName: state.schoolName,



   }
}
function mapDispatchToProps(dispatch) {
   return {
      lessonDataAll: (data1) => dispatch({type: rootConstants.LESSONS_DATA , data: data1}),
      chartState: (data1) => dispatch({type: rootConstants.MODAL_REQUEST , data: data1}),

   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrints);
