import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
const whiteStyle = {
   background: 'white'
 };
 const redStyle = {
   background: 'blue'
 };
class dataPrints extends React.Component {
   constructor() {
      super();
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.state = {
         data: [],
         colors:"white",
         colors1:"red",
         hover: false
      }
   }
   hoverOff(id){
      this.props.lessonDataPush({id})
      this.setState({hover: true});

   }
   hoverOn(id){
      this.props.lessonDataPop({id})
      this.setState({hover: false});
   }




   render() {

      return (
         <table style={this.state.hover ? redStyle : whiteStyle} className={this.props.data.id}
                onClick={() => this.state.hover ? this.hoverOn(this.props.data.id) : this.hoverOff(this.props.data.id)}>
            <tr style={this.state.hover ? redStyle : whiteStyle} >
                  <td>{this.props.data.index}-</td>
                  <td>{this.props.data.lesson_name}-</td>
                  <td>({this.props.data.kridit})-</td>
                  <td>{this.props.data.season}</td>
                  <td>{JSON.stringify(this.props.lessonData)}</td>
            </tr>
         </table>
      );
   }
}
function mapDispatchToProps(dispatch) {
   return {
      lessonDataPush: (data1) => dispatch({type: rootConstants.LESSONS_DATA_PUSH , data: data1}),
      lessonDataPop: (data1) => dispatch({type: rootConstants.LESSONS_DATA_POP , data: data1}),

   }
}



export default connect(allActions.mapStateToProps,mapDispatchToProps)(dataPrints);
