import React from 'react';
import {connect} from 'react-redux';
import {allActions} from '../../_actions'

class OnePlanLessonDataPrint extends React.Component {
   constructor() {
      super();
      this.addfunction = this.addfunction.bind(this);
      this.state = {
         data: [],
         colors:"white",
         colors1:"red",
         addIsLog: false,
         teacherName: "Багш нэр"
      }
   }
   addfunction(data){
      if(data == true)
      {
         this.setState({addIsLog:data})
      }
      else
      {
         this.setState({addIsLog:data})
      }

 }


   render() {

      return (
        <div className="row planBody shadow p-3 mb-5 bg-white rounded" 
                        key={this.props.data.dataJson.id} 
                        data-grid={{x: 0, y: 1, w: 12, h: 2 }} >
            <div className="col-sm-6">{this.props.data.dataJson.lesson_name} - ({this.props.data.dataJson.kridit})</div>
            <div className="col-sm-3">
                {this.props.data.stateJson.teacher}    
            </div>
            <div className="col-sm-3">
                <button class="btn" onClick={() => this.props.planLessonDataPop(this.props.data)}><i class="fa fa-minus-circle"></i>remove</button>
                {this.state.addIsLog ? 
                <button class="btn" onClick={() => this.addfunction(false)}><i class="fa fa-minus-circle"></i>Багш</button> : 
                <button class="btn" onClick={() => this.addfunction(true)}><i class="fa fa-plus-circle"></i>Багш</button>}
            </div>

        </div>


      );
   }
}



export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(OnePlanLessonDataPrint);
