import React from 'react';
import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';

class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
           data: null,
        }
     }
     handleClick(person) {
        this.setState({data: person})
      }
     render() {
        return (
           <div className="App-header">
               Just clicked: <Dataprint data={this.state.data}/>
              <table>
                    {LessonData.map((person, i) => 

                    <p key = {i} data = {person} onClick={() => this.handleClick(i)}>
                        {person.id}
                    </p>

                    )}
              </table>
              <Link to="/">dar</Link>

              )}
           </div>
        );
     }
}


export default homeScreen;
