import {rootConstants} from '../_constants';

const initialState = {
  lessonData: [],
  counter: 247,
  islog: false,
  Reqislog: false,
  graphLessonName: 'ss',
  graphLessonData: [],
  schoolName: 'MUIS',

}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { counter: 1111 ,lessonData: action.data };
    case rootConstants.ROOT_SUCCESS:
        return {counter: 111222};
    case rootConstants.MODAL_REQUEST:
        return { counter: 3333, Reqislog: true, graphLessonName: action.data };
    case rootConstants.MODAL_TRUE:
        return { counter: 441111, islog: true, graphLessonData: action.data };
    case rootConstants.MODAL_FALSE:
        return { counter: 444222, islog: false};
    case rootConstants.SCHOOL_NAME:
        return { counter: 5555111, schoolName: action.data};
    case rootConstants.LESSONS_DATA:
        return { counter: 66661111, lessonData: initialState.lessonData.push(action.data)};
    default:
      return state
  }
}
