        super(props);
        this.state = {
            hariu: 100,
            question:[]
        }
        this.handleClick = this.handleClick.bind(this);
    }
    componentDidMount() {
            fetch('http://192.168.1.104:3001/login', {
                method: 'POST',
                headers: {
                  "Content-type": "application/x-www-form-urlencoded; charset=UTF-8"
                },
                body: 	 'Анхан шатны солонгос хэлний үгийн сан'
              })
            .then(res => res.json())



    }
    handleClick() {
        document.getElementsByClassName("103")[0].style.color = 'red';
    }
    render(){
        return (
            <div>
                     <div className="103"><h1>{this.state.hariu}</h1></div>
                     <button onClick={()=> this.handleClick()}>sss</button>
            </div>

        );

    }
}

export default Hello;
