import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
import LessonDatas from '../_data/lessonData';

class dataPrints extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.modalState = this.modalState.bind(this);
      this.state = {
         data: [],
         colors:"white",
         activeStatus: false,
         bluecolor:{background: "blue"},
         whitecolor:{background: "white"}
      }
   }
   handleClick(person) {
      alert("sdfsd")
   }
   hoverOn(data){
      if(this.state.activeStatus)
      {
         this.setState({  activeStatus: false});
         this.state.data.pop({"id":data})



      }else
      {  
         this.setState({ activeStatus: true});
         this.state.data.push({"id":data})
      }

   }
   hoverOff(){
      this.setState({ colors: "white" });
   }

   modalState(){
      alert(JSON.stringify(this.state.data))
   }



   render() {

      return (
         <div>
            {LessonDatas.map((person, i) => 
            <table style={this.state.activeStatus ? this.state.bluecolor : this.state.whitecolor} className={person.id}
                  onClick={() => this.hoverOn(person.id)}>
               <tr style={{background: this.state.colors}} >
               <td>{person.index}-</td>
                     <td>{person.lesson_name}-</td>
                     <td>({person.kridit})-</td>
                     <td>{person.season}</td>
                     <td>{JSON.stringify(this.state.data)}</td>
               </tr>
            </table>
            )}
         </div>
      );
   }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter,
      islog: state.islog,
      Reqislog: state.Reqislog,
      graphLessonName: state.graphLessonName,
      graphLessonData: state.graphLessonData,
      schoolName: state.schoolName,



   }
}
function mapDispatchToProps(dispatch) {
   return {
      newLesson: (data1) => dispatch({type: rootConstants.ROOT_REQUEST , data: data1}),
      chartState: (data1) => dispatch({type: rootConstants.MODAL_REQUEST , data: data1}),

   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrints);
