        this.state = {
            data: {},
            colors: 'white'

        }
     }
     componentDidMount() {
         this.setState({data: LessonData[0]})

     }
     handleClick(person) {

         this.setState({
            data: person,
            colors:"#00C49F"
         })

      }
     render() {
        return (
           <div className="App-header">
