import React from 'react';
import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';
import dataPrint from './../_components/dataPrint';
const data11 = [];
const data22 = [];
const data33 = [];
const data44 = [];
const data55 = [];
class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            data1: [],
            data2: [],
            data3: [],
            data4: [],
            data5: [],
            colors: 'white'

        }
     }
     componentDidMount() {

      for (var i = 0; i < LessonData.length; i++)
      {
            if(LessonData[i].level == 1 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data11.push(LessonData[i]);
            }
            if(LessonData[i].level == 2 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data22.push(LessonData[i]);
            }
            if(LessonData[i].level == 3 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data33.push(LessonData[i]);
            }
            if(LessonData[i].level == 4 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data44.push(LessonData[i]);
            }else{
               data55.push(LessonData[i]);
            }

      }

      this.setState({data1: data11, 
                     data2: data22,
                     data3: data33,
                     data4: data44,
                     data5: data44,})

     }

     handleClick(person) {

         this.setState({
            data: person,
            colors:"#00C49F"
         })

      }
     render() {
        return (
           <div className="App-header">
                  Just clicked: <Dataprint data={this.state.data}/>

                    {this.state.data1.map((person, i) => 

                    <tr style={{ background: this.state.colors }} 
                        className={person.id} 
                        key = {i}  
                        onClick={() => this.handleClick(person)}

                     >
                        <td>{person.id}</td>
                        <td>{person.index}</td>
                        <td>{person.kridit}</td>
                        <td>{person.lesson_name}</td>
                        <td>{person.parent}</td>
                        <td>{person.level}</td>
                        <td>{person.season}</td>
                        <td>{person.consumption}</td>
                    </tr>

                    )}
              <Link to="/">dar</Link>

              )}
           </div>
        );
     }
}


export default homeScreen;
