import React from 'react';
import CanvasJSReact from '../canvas.js/canvasjs.react';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class amountChart extends React.Component {
	constructor() {
		super();
		this.toggleDataSeries = this.toggleDataSeries.bind(this);
	}

	toggleDataSeries(e){
		if (typeof(e.dataSeries.visible) === "undefined" || e.dataSeries.visible) {
			e.dataSeries.visible = false;
		}
		else{
			e.dataSeries.visible = true;
		}
		this.chart.render();
	}

	render() {
		const datass = this.props.chartsdata
		const options = {
			theme: "light2",
			animationEnabled: true,
			title:{
				text: "Дүн"
			},
			subtitles: [{
				text: ""
			}],
			axisX: {
                title: "States",
			},
			axisY: {
				title: "2018 намар",
				titleFontColor: "#6D78AD",
				lineColor: "#6D78AD",
				labelFontColor: "#6D78AD",
				tickColor: "#6D78AD",
				includeZero: false
			},
			axisY2: {
				title: "2018 хавар",
				titleFontColor: "#51CDA0",
				lineColor: "#51CDA0",
				labelFontColor: "#51CDA0",
				tickColor: "#51CDA0",
				includeZero: false
			},
			toolTip: {
				shared: true
			},
			legend: {
				cursor: "pointer",
				itemclick: this.toggleDataSeries
			},
			data: [{
				type: "spline",
				name: "Намар",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "#,##0 ",
				dataPoints: this.props.chartsdata
			},
			{
				type: "spline",
				name: "Хавар",
				axisYType: "secondary",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "",
				dataPoints: [
					{ label: "A", y: 1 },
					{ label: "B", y: 8 },
					{ label: "C", y: 2 },
					{ label: "D", y: 9 },
					{ label: "F", y: 3 },
					{ label: "W", y: 2 },

				]
			}]
		}


		return (
		<div>
			<CanvasJSChart options = {options} onRef={ref => this.chart = ref}/>
		</div>
		);
	}

}

export default amountChart;
