      }

    render () {
      return (
        <div style={{background:"#e6e6e6"}} >

           <ReactModal 
             isOpen={this.props.planLessonDataModalIsLog}
             className=""
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.planLessonDataModalFalse()}
            >
            <div  className="col-sm-12 layout">
              <div  className="row " key="2v" data-grid={{x: 3, y: 0, w: 3, h: 2,static: true}}>
                <div className="col-sm-12 planBody shadow p-3 mb-5 bg-white rounded" ><h1 class="text-primary" >Та сургуулиа сонгоно уу !!!</h1></div>

                {this.props.planLessonData.map((person, i) => 
                      <div className="col-sm-12 planBody shadow p-3 mb-5 bg-white rounded" >
                          {person.lesson_name} - ({person.kridit})
                          <button class="btn" onClick={() => this.props.planLessonDataPop(person)}><i class="fa fa-minus-circle"></i>remove</button> 
                      </div>
                )}
              </div>  
            </div>  






           </ReactModal>
        </div>
      );
    }
  }

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(OnePlanLessonModal);
