import {rootConstants} from '../_constants';

const initialState = {
  lessonData: {"id":[]},
  lessonData1: {"id":[]},
  counter: 247,

  GrapIsLog: false,
  GrapRequestIsLog: false,
  LessonIsLog: false,
  SchoolIsLog: true,

  LessonChooseRequest: false,
  LessonChooseIsLog: false,
  LessonChooseData: [],

  graphLessonName: 'ss',
  graphLessonData: [],

  schoolName: 'MUIS',

  buttonStateName: 'GRAPH',

  allLessonData:[{"data1":[]},{"data2":[]},{"data3":[]},{"data4":[]},{"data5":[]}],
  lessonPlan:[{"autumn1":[]},
                {"spring1":[]},
                {"autumn2":[]},
                {"spring2":[]},
                {"autumn3":[]},
                {"spring3":[]},
                {"autumn4":[]},
                {"spring4":[]}],
  planRequest: false,
  planIslog: false,

  beforeNumber: null,
  beforeNumberColor: null,
  afterNumber:null,

  planLessonData: [],
  planLessonData1: [],


  example:[]

}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { ...state, counter: 1111 };
    case rootConstants.ROOT_SUCCESS:
        return { ...state, counter: 111222};


    case rootConstants.MODAL_SCHOOL_TRUE:
        return { ...state, counter: 3333, SchoolIsLog: true };
    case rootConstants.MODAL_SCHOOL_FALSE:
        return { ...state, counter: 3333, SchoolIsLog: false, LessonIsLog:true };


    case rootConstants.MODAL_LESSON_TRUE:
        return { ...state, counter: 3333, LessonIsLog: true };
    case rootConstants.MODAL_LESSON_FALSE:
        return { ...state, counter: 3333, LessonIsLog: false };


    case rootConstants.MODAL_GRAP_REQUEST:
        return { ...state, counter: 441111, GrapRequestIsLog: true, graphLessonName: action.data };
    case rootConstants.MODAL_GRAP_TRUE:
        return { ...state, counter: 441111, GrapIsLog: true, graphLessonData: action.data };
    case rootConstants.MODAL_GRAP_FALSE:
        return { ...state, counter: 444222, GrapIsLog: false};

// hicheel songolt huuhduud bagshid ogoh sanal
    case rootConstants.LESSON_CHOOSE_REQUEST:
        return { ...state, counter: 441111, LessonChooseRequest: true, graphLessonName: action.name };
    case rootConstants.LESSON_CHOOSE_TRUE:
        return { ...state, counter: 441111, LessonChooseIsLog: true, LessonChooseData: action.data };
    case rootConstants.LESSON_CHOOSE_FALSE:
        return { ...state, counter: 444222, LessonChooseIsLog: false};




    case rootConstants.SCHOOL_NAME:
        return { ...state, counter: 5555111, schoolName: action.data};


    case rootConstants.LESSON_SET_DATA:
        return { ...state, counter: 99999999999999, allLessonData: action.data};



    case rootConstants.LESSONS_DATA_PUSH:
        return { ...state, counter: 66661111, lessonData1: initialState.lessonData["id"].push(action.data.id)};
    case rootConstants.LESSONS_DATA_POP:
        return { ...state, counter: 666622222, lessonData1: initialState.lessonData["id"].splice(initialState.lessonData["id"].indexOf(action.data.id), 1 )};

    case rootConstants.BUTTON_STATE:
        return { ...state, counter: 999922222, buttonStateName: action.data};

// hicheel huviar gargah uyiin udirdlaga heseg 
    case rootConstants.LESSON_PLAN_REQUEST:
        return { ...state, counter: 441111, planRequest: true };
    case rootConstants.LESSON_PLAN_TRUE:
        return { ...state, counter: 441111, planIslog: true, lessonPlan: action.data };


// omnoh hesgig arilgah husnegtudin id hadgalah
    case rootConstants.BEFORE_NUMBER_SET:
        return { ...state, counter: 441111, beforeNumber: action.data , beforeNumberColor: action.colors};
    case rootConstants.AFTER_NUMBER_SET:
        return { ...state, counter: 441111, afterNumber: action.data };


// neg simistrin hijelin songoson hijeluuud 
    case rootConstants.PLAN_LESSONS_DATA_PUSH:
        return { ...state, counter: 66661111, lessonData1: initialState.lessonData["id"].push(action.data.id)};
    case rootConstants.PLAN_LESSONS_DATA_POP:
        return { ...state, counter: 666622222, lessonData1: initialState.lessonData["id"].splice(initialState.lessonData["id"].indexOf(action.data.id), 1 )};



        default:
      return state
  }
}
