import React from 'react';
import './classCss.css';
import {  Link } from 'react-router-dom';
import {rootConstants} from '../_constants';
import { connect } from 'react-redux';
import  history  from '../_store/history';
import schoolData from '../_data/schoolData';
import PlanPrint from '../_components/_lessonPlan/planPrint';
import {allActions} from '../_actions'
import GridLayout from 'react-grid-layout';


class classScreen extends React.Component {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
          isState: false,
          isActive: true
        };
      }
      componentDidMount() {
        this.props.lessonPlanRequest("hihah");

      }


      handleClick(data, islog) {
        if(islog){
          this.props.modalSchoolFalse(data)
        }else{
          this.props.newSchoolName(data)
        }

      }

      render() {
        const person = ["a","c","d"];
        const person1 = ["s","aa","dd"];
        return (

          <div>

            <GridLayout className="layout" cols={12} rowHeight={20} width={1300}>
              <div style={{background:"red", border:"20px"}} key="d" data-grid={{x: 0, y: 0, w: 3, h: 1, static: true}}>1 Күрс намар</div>
                {this.props.lessonPlan[0].autumn1.map((person, i) => 
                <div  className={this.state.isActive ? 'item is-active' : 'item'}

                style={{
                  WebkitTransform: `translate3d(${translateX}px, ${translateY}px, 0) scale(${scale})`,
                  transform: `translate3d(${translateX}px, ${translateY}px, 0) scale(${scale})`,
                  zIndex: (row === lastPress && colIndex === currentColumn) ? 99 : visualPosition,
              }}
                  style={{background:"gray"}} 
                  key={person.id} 
                  data-grid={{x: 0, y: 1, w: 3, h: 2 }}
                  >
                 {person.index} - {person.lesson_name} - ({person.kridit})
               </div>)}










              <div style={{background:"red"}} key="2v" data-grid={{x: 3, y: 0, w: 3, h: 1,static: true}}>1 Күрс хавар</div>
                {this.props.lessonPlan[1].spring1.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 3, y: 0, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}


              <div style={{background:"red"}} key="d22" data-grid={{x: 6, y: 0, w: 3, h: 1,static: true}}>2 Күрс намар</div>
                {this.props.lessonPlan[2].autumn2.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 6, y: 0, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}


              <div style={{background:"red"}} key="dxc3" data-grid={{x: 9, y: 0, w: 3, h: 1,static: true}}>2 Күрс хавар</div>
                {this.props.lessonPlan[3].spring2.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 9, y: 0, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}


              <div style={{background:"red"}} key="d3e" data-grid={{x: 0, y: 9, w: 3, h: 1,static: true}}>3 Күрс намар</div>
                {this.props.lessonPlan[4].autumn3.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 0, y: 9, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}



              <div style={{background:"red"}} key="ds3" data-grid={{x: 3, y: 9, w: 3, h: 1,static: true}}>3 Күрс хавар</div>
                {this.props.lessonPlan[5].spring3.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 3, y: 9, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}


              <div style={{background:"red"}} key="ddfd3" data-grid={{x: 6, y: 9, w: 3, h: 1,static: true}}>4 Күрс намар</div>
                {this.props.lessonPlan[6].autumn4.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 6, y: 9, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}

              <div style={{background:"red"}} key="dss3" data-grid={{x: 9, y: 9, w: 3, h: 1,static: true}}>4 Күрс хавар</div>
                {this.props.lessonPlan[7].spring4.map((person, i) => 
                <div style={{background:"gray"}} key={person.id} data-grid={{x: 9, y: 9, w: 3, h: 1}}>{person.index} - {person.lesson_name} - ({person.kridit})</div>)}

            </GridLayout>
            <h1>{this.props.counter}dfdf</h1>
          </div>


        )

    }
}


export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(classScreen);
