import React from 'react';
import CanvasJSReact from '../canvas.js/canvasjs.react';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class amountChart extends React.Component {
	constructor() {
		super();
		this.toggleDataSeries = this.toggleDataSeries.bind(this);
	}


	toggleDataSeries(e){
		if (typeof(e.dataSeries.visible) === "undefined" || e.dataSeries.visible) {
			e.dataSeries.visible = false;
		}
		else{
			e.dataSeries.visible = true;
		}
		this.chart.render();
	}

	render() {
		const options = {
			theme: "light2",
			animationEnabled: true,
			title:{
				text: "Дүн"
			},
			subtitles: [{
				text: ""
			}],
			axisX: {
                title: "Graph",
			},
			axisY: {
				title: this.props.data[2].date + " намар",
				titleFontColor: "#6D78AD",
				lineColor: "#6D78AD",
				labelFontColor: "#6D78AD",
				tickColor: "#6D78AD",
				includeZero: false
			},
			axisY2: {
				title: this.props.data[2].date + " хавар",
				titleFontColor: "#51CDA0",
				lineColor: "#51CDA0",
				labelFontColor: "#51CDA0",
				tickColor: "#51CDA0",
				includeZero: false
			},
			toolTip: {
				shared: true
			},
			legend: {
				cursor: "pointer",
				itemclick: this.toggleDataSeries
			},
			data: [{
				type: "spline",
				name: "Намар",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "#,##0 ",
				dataPoints: this.props.data[1].autumn
			},
			{
				type: "spline",
				name: "Хавар",
				axisYType: "secondary",
				showInLegend: true,
				xValueFormatString: "",
				yValueFormatString: "",
			}]
		}


		return (
		<div>
			<CanvasJSChart options = {options} onRef={ref => this.chart = ref}/>
		</div>
		);
	}

}

export default amountChart;
