import {rootConstants} from '../_constants';

export const allActions = {
    chartState,
};
function chartState(lessonname) {
    return dispatch => {
        dispatch(request({ lessonname }));
    }

    function request(lessonname) { return { type: rootConstants.MODAL_REQUEST, data: lessonname } }
    function success(user) { return { type: rootConstants.MODAL_TRUE, user } }
    function failure(error) { return { type: rootConstants.MODAL_TRUE, error } }
}
