import React from 'react';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import './componentcss.css';
import LessonDatas from '../_data/lessonData';
import DataPrints from './dataPrints';
import {allActions} from '../_actions'

class ModalLesson extends React.Component {
    constructor () {
      super();
      this.state = {
        showModal: false,
        test: 1,
        lesdata:[]

      };

      this.handleOpenModal = this.handleOpenModal.bind(this);
      this.handleCloseModal = this.handleCloseModal.bind(this);
    }


    handleOpenModal () {
     alert(JSON.stringify(this.props.graphLessonData))

    }

    handleCloseModal () {
      this.setState({ showModal: false });
    }

    render () {
      return (
        <div>
          <button onClick={ () => this.props.modalTrue()}>true Modal</button>
          <button onClick={ () => this.props.modalFalse()}>false Modal</button>

          <ReactModal 
             isOpen={this.props.islog}
             onClick={ () => this.garphstate()}
             className=""
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.modalFalse()}
             >
            {LessonDatas.map((person, i) => <DataPrints data={person}></DataPrints>)}
            <button onClick={ () => this.props.modalFalse()}>Болсон</button>
          </ReactModal>
        </div>
      );
    }
  }

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(ModalLesson);
