import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'

class dataPrint extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.modalState = this.modalState.bind(this);
      this.rootStatecolor = this.rootStatecolor.bind(this);
      this.state = {
         data: [],
         colors:"white",
         hover: false
      }
   }
   handleClick(person) {
      alert("sdfsd")
   }
   hoverOn(){
      this.setState({ colors: "blue" });

   }
   hoverOff(){
      this.setState({ colors: "white" });
   }

   modalState(data){
      this.props.chartState(data);
   }

   rootStatecolor(data){
      document.getElementsByClassName(data)[0].style.background = 'black';
      document.getElementsByClassName("105")[0].style.background = 'black';
      document.getElementsByClassName("107")[0].style.background = 'black';
      document.getElementsByClassName("109")[0].style.background = 'black';
   }


   render() {

      return (
         <table style={{background: this.state.colors}} className={this.props.data.id}
                onMouseEnter={this.hoverOn}
                onMouseLeave={this.hoverOff} 
                onClick={() => this.rootStatecolor(this.props.data.id)}>
            <tr style={{background: this.state.colors}} 
                onClick={() => this.props.newLesson(this.props.data)}>
                  <td>{this.props.data.index}-</td>
                  <td>{this.props.data.lesson_name}-</td>
                  <td>({this.props.data.kridit})-</td>
                  <td>{this.props.data.season}</td>
            </tr>
            <button className="myButton"onClick={() => this.props.chartState(this.props.data.lesson_name)}> GRAPH</button>
            <button className="myButton" onClick={() => this.rootStatecolor(this.props.data.id)}> set color</button>
         </table>
      );
   }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter,
      islog: state.islog,
      Reqislog: state.Reqislog,
      graphLessonName: state.graphLessonName,
      graphLessonData: state.graphLessonData,
      schoolName: state.schoolName,



   }
}
function mapDispatchToProps(dispatch) {
   return {
      newLesson: (data1) => dispatch({type: rootConstants.ROOT_REQUEST , data: data1}),
      chartState: (data1) => dispatch({type: rootConstants.MODAL_REQUEST , data: data1}),

   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrint);
