import { rootConstants } from '../_constants';
export const allActions = {
  mapStateToProps,
  mapDispatchToProps
};

function mapStateToProps(state) {
  return {
    lessonData: state.lessonData,
    lessonData1: state.lessonData1,
    counter: state.counter,
    GrapIsLog: state.GrapIsLog,
    GrapRequestIsLog: state.GrapRequestIsLog,
    LessonIsLog: state.LessonIsLog,
    SchoolIsLog: state.SchoolIsLog,
    LessonChooseRequest: state.LessonChooseRequest,
    LessonChooseIsLog: state.LessonChooseIsLog,
    LessonChooseData: state.LessonChooseData,
    graphLessonName: state.graphLessonName,
    graphLessonData: state.graphLessonData,
    schoolName: state.schoolName,
    buttonStateName: state.buttonStateName,
    allLessonData: state.allLessonData,
    example: state.example,
    lessonPlan: state.lessonPlan,
    planRequest: state.planRequest,
    planIslog: state.planIslog,
    beforeNumber: state.beforeNumber,
    beforeNumberColor: state.beforeNumberColor,
    afterNumber: state.afterNumber,
    planLessonData: state.planLessonData,
    planLessonData1: state.planLessonData1
  };
}

function mapDispatchToProps(dispatch) {
  return {
    newSchoolName: datas => dispatch({
      type: rootConstants.SCHOOL_NAME,
      data: datas
    }),
    newLesson: data1 => dispatch({
      type: rootConstants.ROOT_REQUEST,
      data: data1
    }),
    chartState: data1 => dispatch({
      type: rootConstants.MODAL_REQUEST,
      data: data1
    }),
    modalTrue: () => dispatch({
      type: rootConstants.MODAL_GRAP_TRUE
    }),
    modalFalse: () => dispatch({
      type: rootConstants.MODAL_GRAP_FALSE
    }),
    modalRequest: data => dispatch({
      type: rootConstants.MODAL_GRAP_REQUEST,
      data: data
    }),
    LessonChooseTrue: () => dispatch({
      type: rootConstants.LESSON_CHOOSE_TRUE
    }),
    LessonChooseFalse: () => dispatch({
      type: rootConstants.LESSON_CHOOSE_FALSE
    }),
    LessonChooseRequest: (data, name) => dispatch({
      type: rootConstants.LESSON_CHOOSE_REQUEST,
      data: data,
      name: name
    }),
    modalLessonTrue: () => dispatch({
      type: rootConstants.MODAL_LESSON_TRUE
    }),
    modalLessonFalse: (data, data1) => dispatch({
      type: rootConstants.MODAL_LESSON_FALSE,
      data: data,
      data1: data1
    }),
    modalSchoolTrue: data => dispatch({
      type: rootConstants.MODAL_SCHOOL_TRUE,
      data: data
    }),
    modalSchoolFalse: () => dispatch({
      type: rootConstants.MODAL_SCHOOL_FALSE
    }),
    lessonDataPush: data1 => dispatch({
      type: rootConstants.LESSONS_DATA_PUSH,
      data: data1
    }),
    lessonDataPop: data1 => dispatch({
      type: rootConstants.LESSONS_DATA_POP,
      data: data1
    }),
    buttonStateControl: data1 => dispatch({
      type: rootConstants.BUTTON_STATE,
      data: data1
    }),
    // Hicheel huvair gargah uyiin tovchnuuud
    lessonPlanRequest: data => dispatch({
      type: rootConstants.LESSON_PLAN_REQUEST,
      data: data
    }),
    // omnoh hesgin noimrig hadgalah dispatch
    beforeNumberSet: (data, colors) => dispatch({
      type: rootConstants.BEFORE_NUMBER_SET,
      data: data,
      colors: colors
    }),
    afterNumberSet: data => dispatch({
      type: rootConstants.AFTER_NUMBER_SET,
      data: data
    }),
    planLessonDataPush: data1 => dispatch({
      type: rootConstants.PLAN_LESSONS_DATA_PUSH,
      data: data1
    }),
    planLessonDataPop: data1 => dispatch({
      type: rootConstants.PLAN_LESSONS_DATA_POP,
      data: data1
    })
  };
}
