import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
import './alertcss.css';
import $ from 'jquery';


class dataPrint extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.handleClickroot = this.handleClickroot.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.rootStatecolor = this.rootStatecolor.bind(this);
      this.buttonState = this.buttonState.bind(this);
      this.createAlert = this.createAlert.bind(this);
      this.state = {
         data: [],
         colors: "green",
         hover: false,
         lessonLogged: false,
         number: null,
         statecolor: null
      }
   }
   createAlert(title, summary, details, severity, dismissible, autoDismiss, appendToId) {
      var iconMap = {
        info: "fa fa-info-circle",
        success: "fa fa-thumbs-up",
        warning: "fa fa-exclamation-triangle",
        danger: "fa ffa fa-exclamation-circle"
      };

      var iconAdded = false;

      var alertClasses = ["alert", "animated", "flipInX"];
      alertClasses.push("alert-" + severity.toLowerCase());

      if (dismissible) {
        alertClasses.push("alert-dismissible");
      }

      var msgIcon = $("<i />", {
        "class": iconMap[severity] // you need to quote "class" since it's a reserved keyword
      });

      var msg = $("<div />", {
        "class": alertClasses.join(" ") // you need to quote "class" since it's a reserved keyword
      });

      if (title) {
        var msgTitle = $("<h4 />", {
          html: title
        }).appendTo(msg);

        if(!iconAdded){
          msgTitle.prepend(msgIcon);
          iconAdded = true;
        }
      }

      if (summary) {
        var msgSummary = $("<strong />", {
          html: summary
        }).appendTo(msg);

        if(!iconAdded){
          msgSummary.prepend(msgIcon);
          iconAdded = true;
        }
      }

      if (details) {
        var msgDetails = $("<p />", {
          html: details
        }).appendTo(msg);

        if(!iconAdded){
          msgDetails.prepend(msgIcon);
          iconAdded = true;
        }
      }


      if (dismissible) {
        var msgClose = $("<span />", {
          "class": "close", // you need to quote "class" since it's a reserved keyword
          "data-dismiss": "alert",
          html: "<i class='fa fa-times-circle'></i>"
        }).appendTo(msg);
      }

      $('#' + appendToId).prepend(msg);

      if(autoDismiss){
        setTimeout(function(){
          msg.addClass("flipOutX");
          setTimeout(function(){
            msg.remove();
          },1000);
        }, 1000);
      }
    }
   handleClick(person) {
      for(var i=0; i<this.props.lessonData['id'].length; i++)
      {
         if(this.props.lessonData['id'][i] == this.props.data.id)
         {
            this.setState({lessonLogged: true})
            break;
         }
      }
   }

   componentDidMount() {this.setState({colors: this.props.data.color})}
   rootStatecolor(){
      if(this.props.afterNumber != null)
      {
         for(var i=0; i < this.props.afterNumber.length; i++)
         {
            document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.background = this.props.afterNumber[i].color;
            document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.border  = '50px';
            document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.boxShadow  = "0px 0px white";
         }
      }

      if(this.props.data.leaves != null)
      {  const allData = [];
         for(var i=0; i<this.props.data.leaves.length; i++)
         {
            var save = document.getElementsByClassName(this.props.data.leaves[i])[0].style.background;
            var alldataI = {"id":this.props.data.leaves[i], "color": save};
            allData.push(alldataI);
            document.getElementsByClassName(this.props.data.leaves[i])[0].style.background = '#d1d147';
            document.getElementsByClassName(this.props.data.leaves[i])[0].style.border  = '50px';
            document.getElementsByClassName(this.props.data.leaves[i])[0].style.boxShadow  = "10px 10px grey";
         }
         this.props.afterNumberSet(allData)
      }
      else
      {
         alert("Залнамж хичээл байхгүй")
      }
   }
   handleClickroot(data, colors) {
      if(this.props.beforeNumber != null){
         document.getElementsByClassName(this.props.beforeNumber)[0].style.background = this.props.beforeNumberColor;
         document.getElementsByClassName(this.props.beforeNumber)[0].style.border  = '50px';
         document.getElementsByClassName(this.props.beforeNumber)[0].style.boxShadow  = "0px 0px white";
      }

      if(data != null)
      {   
         this.props.beforeNumberSet(data, this.props.data.color)
         document.getElementsByClassName(data)[0].style.background = '#d1d147';
         document.getElementsByClassName(data)[0].style.border  = '50px';
         document.getElementsByClassName(data)[0].style.boxShadow  = "10px 10px grey";
      }
      else
      {
         createAlert('Opps!','Something went wrong','Here is a bunch of text about some stuff that happened.','danger',true,false,'pageMessages');
      }
   }


   buttonState(){

      if(this.props.buttonStateName == "GRAPH")
      {
         this.props.modalRequest(this.props.data.lesson_name)
      }
      if(this.props.buttonStateName == "Zalgamj")
      {  
         if(this.props.beforeNumber != null){
            document.getElementsByClassName(this.props.beforeNumber)[0].style.background = this.props.beforeNumberColor;
            document.getElementsByClassName(this.props.beforeNumber)[0].style.border  = '50px';
            document.getElementsByClassName(this.props.beforeNumber)[0].style.boxShadow  = "0px 0px white";
         }
         this.props.beforeNumberSet(null, null)

         this.rootStatecolor(this.props.data.leaves)
      }
      if(this.props.buttonStateName == "Etseg")
      {
         if(this.props.afterNumber != null)
         {
            for(var i=0; i < this.props.afterNumber.length; i++)
            {
               document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.background = this.props.afterNumber[i].color;
               document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.border  = '50px';
               document.getElementsByClassName(this.props.afterNumber[i].id)[0].style.boxShadow  = "0px 0px white";
            }
         }
         this.props.afterNumberSet(null, null)

         this.handleClickroot(this.props.data.parent, this.props.data.color)
      }
      if(this.props.buttonStateName == "LessonChoose")
      {
         this.props.LessonChooseRequest(this.props.data.index, this.props.data.lesson_name)
      }
   }


   hoverOn(data){
      this.setState({ colors: data });

   }
   hoverOff(data){
      this.setState({ colors: data });
   }

   render() {
      return (
        <div 
             style={{background: this.state.colors}}  
             className={this.props.data.id + " " + "card card-1"}
             onClick={this.buttonState}>
            <a>
              {this.props.data.index}-
              {this.props.data.lesson_name}-
              ({this.props.data.kridit})-
              {this.props.data.season}
              {this.state.number}
            </a>
        </div>

      );
   }
}


export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(dataPrint);
