import {rootConstants} from '../_constants';

export const allActions = {
    login,
};

function login(username) {
    return dispatch => {
        dispatch(request({ username }));

    };

    function request(username) { return { type: rootConstants.ROOT_REQUEST, data: username } }
    function success(user) { return { type: rootConstants.ROOT_REQUEST, user } }
    function failure(error) { return { type: rootConstants.ROOT_REQUEST, error } }
}
