import React from 'react';
import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';

class homeScreen extends React.Component {
    constructor() {
        super();
        this.state = {
           data: null,
           letters: ["b","v"] 
        }
     }
     handleClick(letter) {
        this.setState({ data: letter });
      }
     render() {
        return (
           <div className="App-header">
               Just clicked: {this.state.justClicked}
              <table>
                    {this.state.letters.map((person, i) => 
                    <Dataprint key = {i} data = {person} onClick={() => this.handleClick(i)} />)}
              </table>
              <Link to="/">dar</Link>
           </div>
        );
     }
}


export default homeScreen;
