import {rootConstants} from '../_constants';

const initialState = {
  counter: 1
}
const reducer = (state = initialState, action) => {
  switch (action.type) {
      case 'INCREASE_COUNTER':
          return { counter: state.counter + 1 }
      case 'DECREASE_COUNTER':
          return { counter: state.counter - 1 }
  }
  return state
}
