import React from 'react';
import ReactDOM from 'react-dom';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import DataPrint from './dataPrint';
import AmountChart from '../_components/amountChart';
import qs from 'qs';

import './componentcss.css';
const datasss =[
  {"date": "2018"},
  {"spring":[
              { "label": "A", "y": 20 },
              { "label": "B", "y": 8 },
              { "label": "C", "y": 4 },
              { "label": "D", "y": 5 },
              { "label": "F", "y": 6 },
              { "label": "W", "y": 2 },
              { "label": "I", "y": 2 }
           ]},
  {"autumn":[
              { "label": "A", 'y': 1 },
              { "label": "B", "y": 8 },
              { "label": "C", "y": 2 },
              { "label": "D", "y": 9 },
              { "label": "F", 'y': 3 },
              { "label": "W", "y": 2 },
  ]}

]
const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};
class ModalGraph extends React.Component {
    constructor () {
      super();
      this.state = {
        showModal: false,
        test: 1,
        lesdata:[]

      };

      this.handleOpenModal = this.handleOpenModal.bind(this);
      this.handleCloseModal = this.handleCloseModal.bind(this);
      this.garphstate = this.garphstate.bind(this);
    }

    componentDidMount() {

        fetch('http://localhost:3001/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
          },
          body: 	qs.stringify({'lessonname': this.props.graphLessonName})
        })
        .then((res) => res.json())
        .then((data) => { 
          this.setState({lesdata:data})
        })
        .catch((error) => {
          alert('Requessst failed', error);
        });



    }

    handleOpenModal () {
      this.setState({ showModal: true });
    }

    handleCloseModal () {
      this.setState({ showModal: false });
    }
    garphstate () {
      fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
        },
        body: 	qs.stringify({'lessonname': this.props.graphLessonName})
      })
      .then((res) => res.json())
      .then((data) => { 
        this.setState({lesdata:data})
      })
      .catch((error) => {
        alert('Requessst failed', error);
      });
    }

    render () {

      return (
        <div>
          <ReactModal 
             isOpen={this.props.islog}
             onClick={ () => this.garphstate()}
             contentLabel="Дүнгийн график"
             className="Modal"
             style={customStyles}
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.modalFalse()}
             >
            <div >モーダル{[this.props.counter]}</div>
            <AmountChart className="amountChart" data = {this.state.lesdata.datedata}></AmountChart>
            <button onClick={ () => this.props.modalFalse()}>Close Modal</button>
            <button onClick={ () => this.garphstate()}>reefresh</button>
            <button onClick={ () => this.garphstate()}>reefresh</button>
            <button onClick={ () => this.garphstate()}>reefresh</button>
            <button onClick={ () => this.garphstate()}>reefresh</button>
            <button onClick={ () => this.garphstate()}>reefresh</button>
          </ReactModal>
        </div>
      );
    }
  }

  function mapStateToProps(state) {
    return {
       lessonData: state.lessonData,
       counter: state.counter,
       islog: state.islog,
       graphLessonName: state.graphLessonName

    }
 }
 function mapDispatchToProps(dispatch) {
    return {
        modalTrue: () => dispatch({type: rootConstants.MODAL_TRUE }),
        modalFalse: () => dispatch({type: rootConstants.MODAL_FALSE }),
    }
 }
  export default connect(mapStateToProps,mapDispatchToProps)(ModalGraph);
