            colors: 0

        }
     }
     componentDidMount() {
      for (var i = 0; i < LessonData.length; i++)
      {
            this.setState({data: i}) 
      }


     }

     handleClick(person) {

         this.setState({
            data: person,
            colors:"#00C49F"
         })

      }
     render() {
        return (
           <div className="App-header">
                 <Dataprint data={this.state.data} />
                  tpp = {this.state.colors}

              <Link to="/">dar</Link>

           </div>
        );
     }
}


export default homeScreen;
