            question:[]
        }
        this.handleClick = this.handleClick.bind(this);
    }
    componentDidMount() {
        fetch('http://192.168.1.104:3001/login', {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({

              lessonname: "admin",
            })
         }).then((res) => res.json())
               .then((data) => {
                  if(data.token != null)
                  {  
                    alert("Нэвтрэх боломжгүй байнasdasdsadasdsaа")
                  }else{
                     alert("Нэвтрэх боломжгүй байна")
                  }
               }).catch((error) => {
                 alert("Интернэт холболтоо шалганаhh")
               });



    }
    handleClick() {
        document.getElementsByClassName("103")[0].style.color = 'red';
    }
    render(){
        return (
            <div>
                     <div className="103"><h1>{this.state.hariu}</h1></div>
                     <button onClick={()=> this.handleClick()}>sss</button>
            </div>

        );

    }
}

export default Hello;
