import React from 'react';
import CanvasJSReact from '../canvas.js/canvasjs.react';
import qs from 'qs';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions';

import { connect } from 'react-redux';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class LessonChooseChart extends React.Component {
    render() {
		const options = {
			animationEnabled: true,
			title: {
				text: "Customer Satisfaction"
			},
			subtitles: [{
				verticalAlign: "center",
				fontSize: 24,
				dockInsidePlotArea: true
			}],
			data: [{
				type: "doughnut",
				indexLabel: "{name}: {y}",
				yValueFormatString: "#,###'%'",
				dataPoints: [
					{ name: "Unsatisfied", y: 5 },
					{ name: "Very Unsatisfied", y: 31 },
					{ name: "Very Satisfied", y: 40 },
					{ name: "Satisfied", y: 17 },
					{ name: "Neutral", y: 7 }
				]
			}]
		}
		return (
		<div>
			<CanvasJSChart options = {options}
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
		);
	}
}

export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(LessonChooseChart);
