import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import './componentcss.css';
import LessonDatas from '../_data/lessonData';

class ModalLesson extends React.Component {
    constructor () {
      super();
      this.state = {
        showModal: false,
        test: 1,
        lesdata:[]

      };

      this.handleOpenModal = this.handleOpenModal.bind(this);
      this.handleCloseModal = this.handleCloseModal.bind(this);
    }


    handleOpenModal () {
     alert(JSON.stringify(this.props.graphLessonData))

    }

    handleCloseModal () {
      this.setState({ showModal: false });
    }

    render () {
        //Modal Config
    const customStyles = {
        content : {
          top: '50%',
          left: '50%',
          right: 'auto',
          bottom: 'auto',
          marginRight: '-50%',
          transform: 'translate(-50%, -50%)'
        },
        overlay: {
          zIndex: 10
        }
      };
      return (
        <div>
          <button onClick={ () => this.props.modalTrue()}>true Modal</button>

          <ReactModal 
              isOpen = { this.props.islog }
              style = { customStyles } >

              <p>Content</p>
