              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>
              <p>Content</p>

          </ReactModal>
        </div>
      );
    }
  }

  function mapStateToProps(state) {
    return {
       lessonData: state.lessonData,
       counter: state.counter,
       islog: state.islog,
       Reqislog: state.Reqislog,
       graphLessonName: state.graphLessonName,
       graphLessonData: state.graphLessonData,
       schoolName: state.schoolName,
    }
 }
 function mapDispatchToProps(dispatch) {
    return {
        modalTrue: () => dispatch({type: rootConstants.MODAL_TRUE }),
        modalFalse: () => dispatch({type: rootConstants.MODAL_FALSE }),
    }
 }
  export default connect(mapStateToProps,mapDispatchToProps)(ModalLesson);
