import {rootConstants} from '../_constants';

const initialState = {
  lessonData: {"id":[]},
  lessonData1: {"id":[]},
  counter: 247,

  GrapIsLog: false,
  GrapRequestIsLog: false,
  LessonIsLog: false,
  SchoolIsLog: true,

  graphLessonName: 'ss',
  graphLessonData: [],

  schoolName: 'MUIS',

  buttonStateName: 'GRAPH',

  allLessonData:[{data1:[]},{data2:[]},{data3:[]},{data4:[]},{data5:[]}]

}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { ...state, counter: 1111 };
    case rootConstants.ROOT_SUCCESS:
        return { ...state, counter: 111222};


    case rootConstants.MODAL_SCHOOL_TRUE:
        return { ...state, counter: 3333, SchoolIsLog: true };
    case rootConstants.MODAL_SCHOOL_FALSE:
        return { ...state, counter: 3333, SchoolIsLog: false, LessonIsLog:true };


    case rootConstants.MODAL_LESSON_TRUE:
        return { ...state, counter: 3333, LessonIsLog: true };
    case rootConstants.MODAL_LESSON_FALSE:
        return { ...state, counter: 3333, LessonIsLog: false };


    case rootConstants.MODAL_GRAP_REQUEST:
        return { ...state, counter: 441111, GrapRequestIsLog: true, graphLessonName: action.data };
    case rootConstants.MODAL_GRAP_TRUE:
        return { ...state, counter: 441111, GrapIsLog: true, graphLessonData: action.data };
    case rootConstants.MODAL_GRAP_FALSE:
        return { ...state, counter: 444222, GrapIsLog: false};


    case rootConstants.SCHOOL_NAME:
        return { ...state, counter: 5555111, schoolName: action.data};


    case rootConstants.LESSONS_DATA_PUSH:
        return { ...state, counter: 66661111, lessonData1: initialState.lessonData["id"].push(action.data.id)};
    case rootConstants.LESSONS_DATA_POP:
        return { ...state, counter: 666622222, lessonData1: initialState.lessonData["id"].splice(initialState.lessonData["id"].indexOf(action.data.id), 1 )};

    case rootConstants.BUTTON_STATE:
        return { ...state, counter: 999922222, buttonStateName: action.data};
    default:
      return state
  }
}
