import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';

class dataPrint extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.state = {
         data: [],
         colors:"white",
         hover: false
      }
   }
   handleClick(person) {
      alert("sdfsd")
   }
   hoverOn(){
      document.getElementsByClassName(this.props.data.id)[0].style.color = 'red';

   }
   hoverOff(){
      this.setState({ colors: "white" });
   }

   render() {

      return (
         <table style={{background: this.state.colors}} className= {this.props.data.id}
                onMouseEnter={this.hoverOn}
                onMouseLeave={this.hoverOff} >
            <tr 
                  style={{background: this.state.colors}}
                  onClick={() => this.props.newLesson(this.props.data)}>
               <td>{this.props.data.index}-</td>
               <td>{this.props.data.lesson_name}-</td>
               <td>({this.props.data.kridit})-</td>
               <td>{this.props.data.season}</td>


            </tr>
            <button onClick={() => this.props.modalTrue()}> GRAPH</button>
         </table>
      );
   }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter,
      islog: state.islog
   }
}
function mapDispatchToProps(dispatch) {
   return {
      newLesson: (data1) => dispatch({type: rootConstants.ROOT_REQUEST , data: data1}),
      modalTrue: () => dispatch({type: rootConstants.MODAL_TRUE }),

   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrint);
