import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';

class dataPrint extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
           data: [],
           colors:"red"
        }
     }
     handleClick(person) {
        alert("sdfsd")
   }

    render() {

       return (
          <tr className={this.props.data.id} style={{ background: this.state.colors }} onClick={() => this.props.newLesson(this.props.data)}>
             <td>{this.props.data.index}-</td>
             <td>{this.props.data.lesson_name}-</td>
             <td>({this.props.data.kridit})-</td>
             <td>{this.props.data.season}</td>
          </tr>
       );
    }
 }
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter
   }
}
function mapDispatchToProps(dispatch) {
   return {
      newLesson: (data) => dispatch({type: rootConstants.ROOT_REQUEST , data: data}),
   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrint);
