import React from 'react';
import './appcss.css';
import { Grid } from 'gymnast';
import { connect } from 'react-redux';
import Dataprint from './../_components/dataPrint';
import LessonDatas from '../_data/lessonData';
import ModalGraph from '../_components/modalgraph';
import {allActions} from '../_actions';
import ModalSchool from '../_components/modalSchool';
import ModalLesson from '../_components/modalLesson';
import LessonChooseGraph from '../_components/LessonChooseGraph';


class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            colors: 'white',
            colors1: 'green'

        };
     }



     handleClick() {
     }
     render() {
        return (
         <div class="container-fluid " style={{background:"white"}}>
               <LessonChooseGraph></LessonChooseGraph>
               <ModalGraph></ModalGraph>
               <ModalSchool></ModalSchool>
               <ModalLesson></ModalLesson>
            <div class="row">
               <div class="col-sm-3 p-3 mb-2 bg-info text-white" ><a >Таны Судалсан хичээл</a></div>
               <div class="col-sm-3 p-3 mb-2 bg-danger text-white" ><a >Үзэх боломжгүй хичээл</a></div>
               <div class="col-sm-3 p-3 mb-2 bg-success text-white" ><a >Үзэх боломжтой хичээл</a></div>
               <div class="col-sm-3 p-3 mb-2 bg-warning text-dark"><a >Залгамж болон Өмнөх хичээл</a></div>
            </div>
            <div class="row">
               <div class="col-sm-12">
                  <button className="myHeaderButton" type="button" class="btn btn-outline-primary btn-lg btn-block" onClick={() => this.props.planLessonDataModalTrue}>Миний үзэх хичээлүүд</button>
               </div>
            </div>
            <div class="row">
               <div class="col-sm-3">
                  <button className="myHeaderButton" type="button" class="btn btn-outline-primary btn-lg btn-block" onClick={() => this.props.buttonStateControl("LessonChoose")}>LEsson songolt bgash</button>
               </div>
               <div class="col-sm-3">
                  <button className="myHeaderButton" type="button" class="btn btn-outline-primary btn-lg btn-block" onClick={() => this.props.buttonStateControl("GRAPH")}>GRAPH</button>
               </div>
               <div class="col-sm-3">
                  <button className="myHeaderButton" type="button" class="btn btn-outline-primary btn-lg btn-block" onClick={() => this.props.buttonStateControl("Zalgamj")}>Zalgamj</button>
               </div>
               <div class="col-sm-3">
                  <button className="myHeaderButton" type="button" class="btn btn-outline-primary btn-lg btn-block" onClick={() => this.props.buttonStateControl("Etseg")}>Etseg</button>
               </div>
            </div>

            <div class="row">
               <div class="col-sm-3">
                  <div class="col-sm-12">1 Күрсийн үзвэл зохих хичээл</div>
                  <div class="col-sm-12">
                     {this.props.allLessonData[0].data1.map((person, i) => <Dataprint data={person}></Dataprint>)}
                  </div>
               </div>
               <div class="col-sm-3">
                  <div class="col-sm-12">2 Күрсийн үзвэл зохих хичээл</div>
                  <div class="col-sm-12">
                     {this.props.allLessonData[1].data2.map((person, i) => <Dataprint data={person}></Dataprint>)}
                  </div>
               </div>
               <div class="col-sm-3">
                  <div class="col-sm-12">3 Күрсийн үзвэл зохих хичээл</div>
                  <div class="col-sm-12">
                     {this.props.allLessonData[2].data3.map((person, i) => <Dataprint data={person}></Dataprint>)}
                  </div>
               </div>
               <div class="col-sm-3">
                  <div class="col-sm-12">4 Күрсийн үзвэл зохих хичээл</div>
                  <div class="col-sm-12">
                     {this.props.allLessonData[3].data4.map((person, i) => <Dataprint data={person}></Dataprint>)}
                  </div>
               </div>
            </div>
            <div class="row">
               <div class="col-sm-12">
                  <div class="col-sm-12">5 Күрсийн үзвэл зохих хичээл</div>
                  <div class="col-sm-12">
                     {this.props.allLessonData[4].data5.map((person, i) => <Dataprint data={person}></Dataprint>)}
                  </div>
               </div>
            </div>


         </div>

        );
     }
}

export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(homeScreen);
