import React from 'react';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import './componentcss.css';
import DataPrints from '../_components/dataPrints'
import schoolData from '../_data/schoolData';
import {allActions} from '../_actions'
class ModalSchool extends React.Component {
    constructor () {
      super();
      this.state = {
        isState: false
      };

      this.handleClick = this.handleClick.bind(this);
    }


    handleClick(data, islog) {

        if(islog){
            this.props.newSchoolName(data)
        }else{
          this.props.newSchoolName(data)
        }

      }

    render () {
      return (
        <div>

           <ReactModal 
             isOpen={this.props.SchoolIsLog}
             className=""
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.modalSchoolFalse()}
            >
                {schoolData[this.props.schoolName].map((person, i) => 
                    <button 
                        onClick={() => this.handleClick(person.id, person.parent)}
                        className="schoolButton">{person.NAME}
                    </button>
                )}
                <button onClick={ () => this.props.modalSchoolFalse()}>Болсон</button>
           </ReactModal>
        </div>
      );
    }
  }

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(ModalSchool);
