import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'
import LessonDatas from '../_data/lessonData';
const whiteStyle = {
   background: 'white'
 };
 const redStyle = {
   background: 'blue'
 };
class dataPrints extends React.Component {
   constructor() {
      super();
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.state = {
         data: [],
         colors:"white",
         colors1:"red",
         hover: false
      }
   }
   hoverOff(id){
      this.props.lessonDataPush({id})
      this.setState({hover: true});

   }
   hoverOn(id){
      this.props.lessonDataPop({id})
      this.setState({hover: false});
   }




   render() {

      return (
         <table>
            {LessonDatas.map((person, i) => 

               <tr style={this.state.hover ? redStyle : whiteStyle} 
                   className={person.id}
                   onClick={() => this.state.hover ? this.hoverOn(person.id) : this.hoverOff(person.id)}>
                     <td>{person.index}-</td>
                     <td>{person.lesson_name}-</td>
                     <td>({person.kridit})-</td>
                     <td>{person.season}</td>
                     <td>{JSON.stringify(this.props.lessonData)}</td>
               </tr>
            )}
         </table>
      );
   }
}



export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(dataPrints);
