import React from 'react';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import './componentcss.css';
import LessonDatas from '../_data/lessonData';

class ModalLesson extends React.Component {
    constructor () {
      super();
      this.state = {
        showModal: false,
        test: 1,
        lesdata:[]

      };

      this.handleOpenModal = this.handleOpenModal.bind(this);
      this.handleCloseModal = this.handleCloseModal.bind(this);
    }


    handleOpenModal () {
     alert(JSON.stringify(this.props.graphLessonData))

    }

    handleCloseModal () {
      this.setState({ showModal: false });
    }

    render () {
      return (
        <div>
          <button onClick={ () => this.props.modalTrue()}>true Modal</button>

          <ReactModal 
             isOpen={this.props.islog}
             onClick={ () => this.garphstate()}
             contentLabel="Дүнгийн график"
             className="Modal"
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.modalFalse()}
             >
            LessonDatas

            {LessonDatas.map((person, i) => <h1>{person.id}</h1>)}


            <div >モーダル{[this.props.counter]} </div>
            <button onClick={ () => this.props.modalFalse()}>Close Modal</button>
          </ReactModal>
        </div>
      );
    }
  }

  function mapStateToProps(state) {
    return {
       lessonData: state.lessonData,
       counter: state.counter,
       islog: state.islog,
       Reqislog: state.Reqislog,
       graphLessonName: state.graphLessonName,
       graphLessonData: state.graphLessonData,
       schoolName: state.schoolName,
    }
 }
 function mapDispatchToProps(dispatch) {
    return {
        modalTrue: () => dispatch({type: rootConstants.MODAL_TRUE }),
        modalFalse: () => dispatch({type: rootConstants.MODAL_FALSE }),
    }
 }
  export default connect(mapStateToProps,mapDispatchToProps)(ModalLesson);
