import React from 'react';
import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';
import dataPrint from './../_components/dataPrint';

class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            colors: 'red'

        }
     }
     handleClick(person) {

         this.setState({
            data: person
         })

      }
     render() {
        return (
           <div className="App-header">
                  Just clicked: <Dataprint data={this.state.data}/>

                    {LessonData.map((person, i) => 

                    <tr style={{ background: this.state.colors }} 
                        className={person.id} 
                        key = {i}  
                        onClick={() => this.handleClick(person)}

                     >
                        <td>{person.id}</td>
                        <td>{person.index}</td>
                        <td>{person.kridit}</td>
                        <td>{person.lesson_name}</td>
                        <td>{person.parent}</td>
                        <td>{person.level}</td>
                        <td>{person.season}</td>
                        <td>{person.consumption}</td>
                    </tr>

                    )}
              <Link to="/">dar</Link>

              )}
           </div>
        );
     }
}


export default homeScreen;
