import React from 'react';
import { Container, Row, Col } from 'reactstrap';

import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';
import dataPrint from './../_components/dataPrint';
import DataPrints from './../_components/dataPrints';
const data11 = [];
const data22 = [];
const data33 = [];
const data44 = [];
const data55 = [];
class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            data1: [],
            data2: [],
            data3: [],
            data4: [],
            data5: [],
            colors: 'white',
            colors1: 'green'

        }
     }
     componentDidMount() {

      for (var i = 0; i < LessonData.length; i++)
      {
            if(LessonData[i].level == 1 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data11.push(LessonData[i]);
            }
            if(LessonData[i].level == 2 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data22.push(LessonData[i]);
            }
            if(LessonData[i].level == 3 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data33.push(LessonData[i]);
            }
            if(LessonData[i].level == 4 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data44.push(LessonData[i]);
            }else{
               data55.push(LessonData[i]);
            }

      }

      this.setState({data1: data11, 
                     data2: data22,
                     data3: data33,
                     data4: data44,
                     data5: data44,})

     }

     handleClick(person, i) {
         this.setState({
            data: person,
            colors:"#00C49F" 
         })

      }
     render() {
        return (
            <Container>
               <Row>
                  <Col>.col</Col>
               </Row>
               <Row>
                  <Col>.col</Col>
                  <Col>.col</Col>
                  <Col>.col</Col>
                  <Col>.col</Col>
               </Row>
               <Row>
                  <Col xs="3">.col-3</Col>
                  <Col xs="auto">.col-auto - variable width content</Col>
                  <Col xs="3">.col-3</Col>
               </Row>
               <Row>
                  <Col xs="6">.col-6</Col>
                  <Col xs="6">.col-6</Col>
               </Row>
               <Row>
                  <Col xs="6" sm="4">.col-6 .col-sm-4</Col>
                  <Col xs="6" sm="4">.col-6 .col-sm-4</Col>
                  <Col sm="4">.col-sm-4</Col>
               </Row>
               <Row>
                   <Col sm={{ size: 6, order: 2, offset: 1 }}>.col-sm-6 .order-sm-2 .offset-sm-1</Col>
               </Row>
               <Row>
                  <Col sm="12" md={{ size: 6, offset: 3 }}>.col-sm-12 .col-md-6 .offset-md-3</Col>
               </Row>
               <Row>
                  <Col sm={{ size: 'auto', offset: 1 }}>.col-sm-auto .offset-sm-1</Col>
                  <Col sm={{ size: 'auto', offset: 1 }}>.col-sm-auto .offset-sm-1</Col>
               </Row>
            </Container>

        );
     }
}


export default homeScreen;
