import React from 'react';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {allActions} from '../../_actions'
import GridLayout from 'react-grid-layout';
import WidthProvider from '../_lessonPlan/WidthProvider'

const ResponsiveGridLayout = WidthProvider(GridLayout);

class OnePlanLessonModal extends React.Component {
    constructor () {
      super();
      this.state = {
        isState: false
      };

      this.handleClick = this.handleClick.bind(this);
    }


    handleClick(data, islog) {


      }

    render () {
      return (
        <div style={{background:"#e6e6e6"}} >

           <ReactModal 
             isOpen={this.props.planLessonDataModalIsLog}
             className="Modal"
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.planLessonDataModalFalse()}
            >
              <div class="text-center">
                <h1 class="text-primary" >Та сургуулиа сонгоно уу !!!</h1>
              </div>

            <ResponsiveGridLayout className="col-sm-12 layout" cols={12} rowHeight={20} width={1300}>
              <div className="font-weight-bold text-center shadow-lg p-3 mb-5 bg-white rounded"  key="d" data-grid={{x: 0, y: 0, w: 12, h: 3, static: true}}>1 Күрс намар</div>
                {this.props.planLessonData.map((person, i) => 
                <div className="planBody shadow p-3 mb-5 bg-white rounded" key={person.id} data-grid={{x: 0, y: 1, w: 12, h: 3 }} >{person.lesson_name} - ({person.kridit})</div>)}

            </ResponsiveGridLayout>






           </ReactModal>
        </div>
      );
    }
  }

  export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(OnePlanLessonModal);
