import React from 'react';
import './classCss.css';
import {  Link } from 'react-router-dom';
import {rootConstants} from '../_constants';
import { connect } from 'react-redux';
import schoolData from '../_data/schoolData';
const elements = <h1>Hello, world</h1>;
const A = 65 ;
class classScreen extends React.Component {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
          isState: false
        };
      }

      handleClick(data, islog) {

        if(islog){
          this.setState({isState: true})
        }else{
          this.props.newSchoolName(data)
        }

      }

      render() {
        if(this.state.isState){
          return (
          <h1 path="/Home" >dfsdfsdfsd</h1>
          )
        }else{
        return (
          <div className="divCss">
            Just clicked: {this.props.counter}
            {schoolData[this.props.schoolName].map((person, i) => 
              <button 
                  onClick={() => this.handleClick(person.id, person.parent)}
                  className="schoolButton">{person.NAME}
              </button>
            )}
          </div>
        )
      }
    }
}






function mapStateToProps(state) {
  return {
     lessonData: state.lessonData,
     counter: state.counter,
     islog: state.islog,
     Reqislog: state.Reqislog,
     graphLessonName: state.graphLessonName,
     graphLessonData: state.graphLessonData,
     schoolName: state.schoolName,
  }
}
function mapDispatchToProps(dispatch) {
  return {
      newSchoolName: (datas) => dispatch({type: rootConstants.SCHOOL_NAME , data:datas}),
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(classScreen);
