import React from 'react';
import './classCss.css';
import {  Link } from 'react-router-dom';
import {rootConstants} from '../_constants';
import { connect } from 'react-redux';
import  history  from '../_store/history';
import schoolData from '../_data/schoolData';
import {allActions} from '../_actions'

class classScreen extends React.Component {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
          isState: false
        };
      }


      handleClick(data, islog) {

        if(islog){
          history.push("/Home");
        }else{
          this.props.newSchoolName(data)
        }

      }

      render() {

        return (
          <div className="divCss">

            {schoolData[this.props.schoolName].map((person, i) => 
              <button 
                  onClick={() => this.handleClick(person.id, person.parent)}
                  className="schoolButton">{person.NAME}
              </button>
            )}
          </div>
        )

    }
}


export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(classScreen);
