import {rootConstants} from '../_constants';

const initialState = {
  lessonData: [],
  counter: 247,
  islog: false,
  Reqislog: false,
  graphLessonName: 'ss',
  graphLessonData: [],
  schoolName: 'MUIS',

}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { ...state, counter: 1111 };
    case rootConstants.ROOT_SUCCESS:
        return { ...state, counter: 111222};
    case rootConstants.MODAL_REQUEST:
        return { ...state, counter: 3333, Reqislog: true, graphLessonName: action.data };
    case rootConstants.MODAL_TRUE:
        return { ...state, counter: 441111, islog: true, graphLessonData: action.data };
    case rootConstants.MODAL_FALSE:
        return { ...state, counter: 444222, islog: false};
    case rootConstants.SCHOOL_NAME:
        return { ...state, counter: 5555111, schoolName: action.data};
    case rootConstants.LESSONS_DATA_PUSH:
        return { ...state, counter: 66661111, counter: initialState.lessonData.push(action.data.id)};
    case rootConstants.LESSONS_DATA_POP:
        return { ...state, counter: 666622222, graphLessonData: initialState.lessonData["id"].splice(initialState.lessonData["id"].indexOf(action.data.id), 1 )};
    default:
      return state
  }
}
