import React from 'react';
import {  Link } from 'react-router-dom';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';

const ids = LessonData.id;
class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
           data: {},
        }
     }
     handleClick(i) {
        this.setState({ data: i });
      }
     render() {
        return (
           <div className="App-header">
               Just clicked: <Dataprint data={this.state.data}/>
              <table>
                    {ids.map((person, i) => 
                    <Dataprint key = {i} data = {person} onClick={() => this.handleClick(i)} />)}
              </table>
              <Link to="/">dar</Link>

              )}
           </div>
        );
     }
}


export default homeScreen;
